mapper.select:
SELECT TS.site_code, TS.site_name, TM.menu_order FROM t_site TS left join t_menu TM on TS.site_code = TM.menu_code WHERE site_code in
#{item}
ORDER BY TM.menu_order ASC

select
	count(*) cnt
from t_site
where site_code = #{siteCode}

	and site_code <> #{id}

mapper.update:
UPDATE t_site SET
	site_name = #{siteName}
WHERE
	site_code = #{id}
