mapper.select:
/* mngrBoardconfigList */
SELECT a.bc_id, a.bc_name, a.bc_skin, a.bc_type, a.bc_topinfo, a.bc_viewcount, a.bc_list, a.bc_view, a.bc_regist, a.bc_update, a.bc_reply, a.bc_mngr_list, a.bc_mngr_view, a.bc_mngr_regist, a.bc_mngr_update, a.bc_mngr_reply, a.bc_replyyn, a.bc_fileyn, a.bc_secretyn, a.bc_noticeyn, a.bc_groupyn, a.bc_groupcode, a.bc_filecount, a.bc_filesize, a.bc_defaultpage, a.bc_editoryn, a.bc_prevnextyn, a.bc_koglyn, a.bc_thumbyn, a.bc_thumbcount, a.bc_thumbwidth, a.bc_thumbheight, a.bc_thumbratioyn, a.bc_extcolumninfo, CAST(A.REGDT as DATE ) AS REGDT, CAST(A.UPDDT as DATE ) AS UPDDT, CAST(A.DELDT as DATE ) AS DELDT, a.regmemid, a.updmemid, a.delmemid, a.delyn, a.bc_RSS, a.bc_comment, (SELECT count(*) FROM t_board WHERE bc_id = a.bc_id AND delyn = 'N') boardCnt FROM t_board_config a WHERE 1=1
ORDER BY
a.bc_id a.bc_name
${@egovframework.itgcms.util.CommUtil@sqlInjetFilter(ordBy)}
ORDER BY regdt DESC

/* mngrBoardconfigCheckId */
SELECT COUNT(*) cnt FROM t_board_config
WHERE bc_id = #{id}

mapper.update:
/* deleteMngrBoardconfigProc */
SELECT CONCAT(#{id} ,DATE_FORMAT(SYSDATETIME, '%y%j%H%i%s')) FROM DB_ROOT
UPDATE t_board_config SET bc_id = #{bcId}, delyn = 'Y', delmemid = #{delmemid}, deldt = CURRENT_TIMESTAMP WHERE bc_id = #{id}

/* deleteMngrBoardconfigChkProc */
UPDATE t_board_config SET bc_id = CONCAT(bc_id ,DATE_FORMAT(SYSDATETIME, '%y%j%H%i%s')), delyn = 'Y', delmemid = #{delmemid}, deldt = CURRENT_TIMESTAMP WHERE bc_id IN
#{item}

mapper.select:
/* mngrBoardconfigListAjax */ SELECT a.bc_id , a.bc_name , a.bc_skin , a.bc_type , a.bc_defaultpage FROM t_board_config a WHERE a.delyn = 'N'
AND ( a.bc_name LIKE '%' || #{schStr} || '%' OR a.bc_id LIKE '%' || #{schStr} || '%' )
ORDER BY a.bc_name
