mapper.select:
/* selectMngrCodeList */ SELECT A.C_CODE, A.C_NAME, A.C_PCODE, A.C_DEPTH, A.C_ORDER, A.C_AUTH, A.ETC1, A.ETC2, CAST(A.REGDT as DATE ) AS REGDT, CAST(A.UPDDT as DATE ) AS UPDDT, CAST(A.DELDT as DATE ) AS DELDT, A.REGMEMID, A.UPDMEMID, A.DELMEMID, A.DEFAULTYN, A.DELYN, A.EXP1, (SELECT COUNT(*) FROM T_CODE WHERE C_PCODE = A.C_CODE AND DELYN = 'N') subtree FROM t_code A WHERE A.DELYN = 'N'
AND A.C_CODE LIKE #{schCode} || '%' AND A.C_DEPTH > '2'
AND A.C_PCODE = #{schCode}
AND A.C_AUTH = '00'
AND A.C_AUTH <= #{cauth}
ORDER BY C_ORDER

/* mngrCodeDupleCheck */
SELECT COUNT(*) CNT FROM T_CODE
WHERE
C_CODE = #{ccode}

mapper.update:
/* mngrCodeUpdate */
UPDATE T_CODE SET
UPDDT = CURRENT_TIMESTAMP,
UPDMEMID = #{updmemid},
C_NAME = #{cname}

	,C_AUTH = #{cauth}


	,ETC1 = #{etc1}


	,ETC2 = #{etc2}


	,EXP1 = #{exp1}

WHERE C_CODE = #{schCode}

/* modMngrCodeChngDefaultAjax */
UPDATE t_code SET
DEFAULTYN = #{defaultyn},
UPDDT = CURRENT_TIMESTAMP,
UPDMEMID = #{updmemid}
WHERE C_CODE = #{schCode}

/* mngrCodeRegistDeleteAjax */

UPDATE t_code SET
c_code = CONCAT(c_code, DATE_FORMAT(SYSDATETIME, '%y%j%H%i%s')),
DELYN = 'Y',
DELDT = CURRENT_TIMESTAMP,
DELMEMID = #{delmemid}
WHERE C_CODE = #{schCode}

/* mngrCodeDeleteSubCodes */

UPDATE t_code SET
c_code = CONCAT(c_code, DATE_FORMAT(SYSDATETIME, '%y%j%H%i%s')),
DELYN = 'Y',
DELDT = CURRENT_TIMESTAMP,
DELMEMID = #{delmemid}
WHERE (C_CODE = #{schCode}

	OR C_PCODE LIKE CONCAT(#{schCode}, '%')

)
AND DELYN ='N'

/* mngrCodeSwap */
UPDATE T_CODE SET
C_ORDER = #{corder}
WHERE
C_CODE = #{ccode}
