/* searchAll >>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>*/
SELECT a.bd_idx, a.bc_id, a.bd_code, a.bd_writer, a.bd_title, a.bd_content, a.bd_readnum, a.bd_passwd, a.bd_secret, a.bd_notice, a.bd_useyn, a.bd_ip, a.bd_phone, a.bd_mobile, a.bd_email, a.bd_addr1, a.bd_addr2, a.bd_reidx, a.bd_relevel, a.bd_reorder, CAST(A.REGDT as DATE ) AS REGDT, CAST(A.UPDDT as DATE ) AS UPDDT, CAST(A.DELDT as DATE ) AS DELDT, a.regmemid, a.updmemid, a.delmemid, a.delyn, a.bd_kogluseyn, a.bd_kogltype1, a.bd_kogltype2, a.bd_notice_termyn, a.bd_notice_sdt, a.bd_notice_edt, a.bd_use_termyn, a.bd_use_sdt, a.bd_use_edt, a.bd_thumb1, a.bd_thumb2, a.bd_thumb3, a.bd_thumb1_alt, a.bd_thumb2_alt, a.bd_thumb3_alt, a.bd_answer, a.bd_answer_writer, a.group_code, a.file_id
FROM t_board a WHERE 1=1 AND ((a.bd_title) LIKE '%' || #{schStr} || '%' OR (a.bd_content) LIKE '%' || #{schStr} || '%') AND a.bc_id = #{id} AND a.delyn = 'N' ORDER BY a.bd_reidx DESC, a.bd_reorder ASC
