mapper.select:
/*selectMyTempleteconfigList*/ SELECT FROM t_templete
sitecode = #{siteCode} and delyn='N'
ORDER BY upddt desc

mapper.update:
/*updateTempleteconfigProc*/
update t_templete set
	temp_code 		= #{tempCode},
	temp_name 		= #{tempName},
	recent_bd_cnt 	= #{recentBdCnt},
	upddt			= sysdate,
	updmemid 		= #{updmemid}
where
	temp_idx 		= #{tempIdx}

/*deleteTempleteconfigProc*/
update t_templete set
	delyn		= 'Y' ,
	deldt		= sysdate,
	delmemid 	= #{delmemid}
where
	temp_idx 	= #{tempIdx}

mapper.select:
/*selectGetTempleteconfig*/
SELECT 
  FROM t_templete

AND	temp_idx = #{tempIdx}
ORDER BY upddt desc
