mapper.select:
/***** mngrManagerCodeListRecursive *****/ select a.c_code, a.c_name, a.etc1, a.etc2, a.exp1, a.c_depth, a.c_pcode, a.c_order,
(select count(*) from t_code b where b.c_pcode = a.c_code and b.delyn = 'N') subtree from t_code a where a.delyn = 'N'
and c_pcode = #{id}
order by a.c_depth, a.c_order

mapper.update:
/* updateMngrOrganChartInfoProc */ UPDATE T_ORGAN_CHART
TOP_CODE = #{topCode}, COLUMN_CNT = #{columnCnt}, COLUMN_NM1 = #{columnNm1}, COLUMN_NM2 = #{columnNm2}, COLUMN_NM3 = #{columnNm3},
where OR_IDX = 1
