mapper.sql:
/*slideItemlistWhere*/

	    delyn = 'N'

	 	AND	slides_idx = #{slidesIdx}


	 	AND	slitem_idx = #{slitemIdx}


	 	AND	useyn = #{useyn}

mapper.update:
/*updateMngrSlidesProc*/
update t_slides set
	slides_code 		= #{slidesCode},
	slides_name 		= #{slidesName},
	slides_type 		= #{slidesType},
	useyn 				= #{useyn},
	upddt				= getdate(),
	updmemid 			= #{updmemid}
where
	slides_idx 			= #{slidesIdx}

/*deleteMngrSlidesProc*/
update t_slides set
	delyn		= 'Y' ,
	deldt		= getdate(),
	delmemid 	= #{delmemid}
where
	slides_idx 	= #{slidesIdx}

/*updateMngrSlideItemProc*/ update t_slide_item set slitem_title = #{slitemTitle}, slitem_img = #{slitemImg}, slitem_imgalt = #{slitemImgalt}, slitem_linkgubun = #{slitemLinkgubun},
slitem_linkTimg = #{slitemLinktimg}, slitem_linkTtxt = #{slitemLinkttxt}, slitem_linktcolor = #{slitemLinktcolor}, slitem_linkSimg = #{slitemLinksimg}, slitem_linkStxt = #{slitemLinkstxt}, slitem_linkscolor = #{slitemLinkscolor}, slitem_linkbtn = #{slitemLinkbtn}, slitem_linkPosHor = #{slitemLinkposhor}, slitem_linkPosVer = #{slitemLinkposver},
slitem_linkurl = #{slitemLinkurl}, slitem_linktype = #{slitemLinktype},
slitem_order = #{slitemOrder}, useyn = #{useyn}, upddt = getdate(), updmemid = #{updmemid} where slitem_idx = #{slitemIdx}

/*deleteMngrSlideItemProc*/
update t_slide_item set
	delyn		= 'Y' ,
	deldt		= getdate(),
	delmemid 	= #{delmemid}
where
	slitem_idx 	= #{slitemIdx}
