mapper.select:
/***** mngrManagerMenuListRecursive *****/ select a.c_code, a.c_name, a.etc1, a.etc2, a.c_depth, a.c_pcode, a.c_order,
#{minDepth} minDepth, #{maxDepth} maxDepth, #{schStr} schStr, #{schLicenseType} schLicenseType, #{act} act, (select count(*) from t_code b where b.c_pcode = a.c_code and b.delyn = 'N') subtree from t_code a where a.delyn = 'N'
and c_pcode = #{id}
and c_code not in ('SITE')
and a.c_auth <= #{schStr} and a.c_depth >= #{minDepth} order by a.c_depth, a.c_order
