mapper.insert:
/***** addAttachFile *****/
SELECT J_ATTACHFILE_SEQ.nextval FROM DUAL
INSERT INTO J_ATTACHFILE ( FILE_IDX, FILE_ID, FILE_SEQ, FILE_NAME, FILE_SIZE, FILE_FOLDER, FILE_MASK, DOWNLOAD_COUNT, DOWNLOAD_EXPIRE_DATE, DOWNLOAD_LIMIT_COUNT, REG_DATE ) VALUES ( #{fileIdx}, #{fileId}, (SELECT NVL(MAX(FILE_SEQ),0)+1 FROM J_ATTACHFILE WHERE FILE_ID = #{fileId}), #{fileName}, #{fileSize}, #{fileFolder}, #{fileMask}, 0, #{downloadExpireDate}, #{downloadLimitCount}, SYSDATE )

mapper.update:
/***** updateAttachFileDeleteYnByFileId *****/
	UPDATE J_ATTACHFILE
	   SET DELETE_YN = #{deleteYn}
	 WHERE FILE_ID   = #{fileId}

	/***** updateAttachFileDeleteYn *****/
		UPDATE J_ATTACHFILE
		   SET DELETE_YN = #{deleteYn}
		 WHERE FILE_ID  = #{fileId}

       		AND FILE_SEQ in

       			#{item}

	/***** updateAttachFileDeleteYnByIdx *****/
		UPDATE J_ATTACHFILE
		   SET DELETE_YN = #{deleteYn}
		 WHERE FILE_IDX in

       			#{item}

	/***** updateAttachFileDownloadCount *****/
		UPDATE J_ATTACHFILE
           SET DOWNLOAD_COUNT = DOWNLOAD_COUNT + 1


		       AND FILE_IDX  = #{fileIdx}


		       AND FILE_ID  = #{fileId}


		       AND FILE_SEQ = #{fileSeq}

	/***** updateAttachFileDownloadCountBySequence *****/
		UPDATE J_ATTACHFILE
           SET DOWNLOAD_COUNT = DOWNLOAD_COUNT + 1
         WHERE FILE_ID        = #{fileId}
           AND FILE_SEQ       = #{fileSeq}

	/***** updateAttachFileDownloadCountByFileId *****/
		UPDATE J_ATTACHFILE
           SET DOWNLOAD_COUNT = DOWNLOAD_COUNT + 1
         WHERE FILE_ID = #{value}

mapper.delete:
	/***** removeAttachFileByIdx *****/
		DELETE
          FROM J_ATTACHFILE
         WHERE FILE_IDX  = #{fileIdx}

	/***** removeAttachFile *****/
		DELETE
          FROM J_ATTACHFILE
         WHERE FILE_ID  = #{fileId}

       		AND FILE_SEQ in

       			#{item}

mapper.select:
/***** selectAttachFile *****/ SELECT FILE_IDX , FILE_ID , FILE_SEQ , FILE_NAME , FILE_SIZE , FILE_FOLDER , FILE_MASK , DOWNLOAD_COUNT , DOWNLOAD_EXPIRE_DATE , DOWNLOAD_LIMIT_COUNT , REG_DATE FROM J_ATTACHFILE
AND FILE_IDX = #{fileIdx}
AND FILE_ID = #{fileId}
AND FILE_SEQ = #{fileSeq}

/***** selectAttachFileSwapTarget *****/
SELECT * FROM ( select a.FILE_IDX, a.FILE_ID, a.FILE_SEQ from J_ATTACHFILE a
DELETE_YN = 'N' AND a.FILE_ID = (select FILE_ID from J_ATTACHFILE where FILE_IDX= #{fileIdx})
and a.FILE_SEQ < (select FILE_SEQ from J_ATTACHFILE where FILE_IDX= #{fileIdx}) order by FILE_SEQ desc
and a.FILE_SEQ > (select FILE_SEQ from J_ATTACHFILE where FILE_IDX= #{fileIdx}) order by FILE_SEQ asc
) WHERE rownum < 2

/***** selectAttachFiles *****/ SELECT FILE_IDX , FILE_ID , FILE_SEQ , FILE_NAME , FILE_SIZE , FILE_FOLDER , FILE_MASK , DOWNLOAD_COUNT , DOWNLOAD_EXPIRE_DATE , DOWNLOAD_LIMIT_COUNT , REG_DATE FROM J_ATTACHFILE
DELETE_YN = 'N'
AND FILE_ID = #{fileId}
AND FILE_SEQ = #{fileSeq}
ORDER BY FILE_SEQ ASC

/***** getAttachFileSeqs *****/
	SELECT FILE_SEQ
	  FROM J_ATTACHFILE
	 WHERE FILE_ID = #{value}
	   AND DELETE_YN = 'Y'

	/***** isExistingAttachFileInfo *****/
		SELECT FILE_ID
		  FROM J_ATTACHFILE
		 WHERE FILE_ID  = #{fileId}

       		AND FILE_SEQ in

       			#{item}

mapper.update:
/***** modAttachFileSeq *****/
	UPDATE 	J_ATTACHFILE SET
			FILE_SEQ = #{fileSeq}
	 WHERE 	FILE_IDX = #{fileIdx}
