/*listWhere*/
DELETE_YN = 'N'
and mber_id like '%' || #{schStr} || '%'
and inqire_id like '%' || #{schStr} || '%'
and inqire_nm like '%' || #{schStr} || '%'
and ( mber_id like'%' || #{schStr} || '%' or inqire_id like '%' || #{schStr} || '%' or inqire_nm like '%' || #{schStr} || '%' )
and to_char(inqire_date, 'YYYY-MM-DD') >= #{schSdt}
and to_char(inqire_date, 'YYYY-MM-DD') <= #{schEdt}
