mapper.sql:
/*listWhere*/

	t1.delyn = 'N'

		AND t1.comem_idx = #{schId}


		AND t1.useyn = #{schUseYn}


		AND t1.regmemid = #{schMemid}




				AND t1.comem_comno = #{schOpt2}


				AND t1.comem_name = #{schOpt2}






				AND t1.comem_name LIKE '%' || #{schStr} || '%'


				AND t1.comem_ceonm LIKE '%' || #{schStr} || '%'


				AND (t1.comem_name LIKE '%' || #{schStr} || '%' OR t1.comem_ceonm LIKE '%' || #{schStr} || '%') AND t1.comem_status = 'com_status_1'


				AND (t1.comem_name LIKE '%' || #{schStr} || '%' OR t1.comem_ceonm LIKE '%' || #{schStr} || '%')

mapper.update:
/*mngrUpdateComemberProc*/ UPDATE t_comember
comem_id =#{comemId}, comem_comno =#{comemComno}, comem_name =#{comemName}, comem_ceonm =#{comemCeonm}, comem_code_type =#{comemCodeType}, comem_code_cate =#{comemCodeCate}, comem_empl =#{comemEmpl}, comem_post =#{comemPost}, comem_addr1 =#{comemAddr1}, comem_addr2 =#{comemAddr2}, comem_chrge_name =#{comemChrgeName}, comem_chrge_grp =#{comemChrgeGrp}, comem_chrge_pos =#{comemChrgePos}, comem_chrge_post =#{comemChrgePost}, comem_chrge_addr1 =#{comemChrgeAddr1}, comem_chrge_addr2 =#{comemChrgeAddr2}, comem_chrge_cell =#{comemChrgeCell}, comem_chrge_tel =#{comemChrgeTel}, comem_chrge_mail =#{comemChrgeMail}, comem_chrge_tname =#{comemChrgeTname}, comem_chrge_tmail =#{comemChrgeTmail}, comem_status =#{comemStatus}, etc1 =#{etc1}, etc2 =#{etc2}, updmemid = #{logMemid}
WHERE comem_idx = #{schId}

mapper.select:
/*mngrGetComemberList */
SELECT t1.comem_idx, t1.comem_id, t1.comem_comno, t1.comem_name, t1.comem_ceonm, t1.comem_code_type, t1.comem_code_cate, t1.comem_empl, t1.comem_post, (t1.comem_addr1||', '||t1.comem_addr2) comem_addr, t1.comem_addr1, t1.comem_addr2, t1.comem_chrge_name, t1.comem_chrge_grp, t1.comem_chrge_pos, t1.comem_chrge_post, t1.comem_chrge_addr1, t1.comem_chrge_addr2, t1.comem_chrge_cell, t1.comem_chrge_tel, t1.comem_chrge_mail, t1.comem_chrge_tname, t1.comem_chrge_tmail, t1.comem_status, t2.c_name comem_status_nm, t1.etc1, t1.etc2, t1.useyn FROM t_comember t1 LEFT OUTER JOIN t_code t2 ON t1.comem_status = t2.c_code
ORDER BY
t1.comem_name t1.comem_ceonm comem_addr t1.comem_chrge_name t1.comem_chrge_tel t1.comem_status
${@egovframework.itgcms.util.CommUtil@sqlInjetFilter(ordBy)}
ORDER BY t1.comem_idx DESC

