mapper.select:
/* getSocialMediaKey */
SELECT k.sm_name, k.sm_appkey, k.sm_secretkey, k.useyn, k.sm_etc1, t.sm_etc2, k.regmemid, k.updmemid, k.delmemid, k.regdt, k.upddt, k.deldt, t.site_code, t.sm_accesstoken, t.sm_accessible, t.sm_userid, t.sm_issueDate FROM t_smaccesstoken t, t_smkeys k WHERE k.sm_name=#{smName} and k.sm_name=t.sm_name and t.site_code = #{siteCode}
SELECT sm_name, sm_appkey, sm_secretkey, useyn, sm_etc1, regmemid, updmemid, delmemid, regdt, upddt, deldt FROM t_smkeys WHERE sm_name = #{smName}

mapper.update:
/* updateSocialmediaKey */
	UPDATE t_smkeys
	SET
		sm_appkey = #{smAppkey},
		sm_secretkey = #{smSecretkey},
		useyn = #{useyn},
		updmemid = #{updmemid},
		delmemid = '',
		upddt = CURRENT_TIMESTAMP,
		deldt = NULL

			, sm_etc1 = #{etc1}


			, sm_etc2 = #{etc2}

	WHERE sm_name = #{smName}

mapper.select:
/* getAccessToken */
	SELECT
		sm_accesstoken
	FROM
		t_smaccesstoken
	WHERE
		sm_name = #{smName}
		and site_code = #{siteCode}

mapper.update:
/* updateAccessToken */
	UPDATE t_smaccesstoken
	SET
		sm_accesstoken = #{smAccesstoken},
		sm_userId = #{smUserid},
		sm_issueDate = CURRENT_TIMESTAMP
	WHERE
		sm_name = #{smName}
		AND site_code =#{siteCode}

/* markAccessible */
	UPDATE t_smaccesstoken
	SET
		sm_accessible = #{smAccessible}

			, sm_etc2 = #{etc2}

	WHERE
		site_code = #{siteCode}
		AND sm_name = #{smName}

/* noUseAccessToken */
	UPDATE t_smaccesstoken
	SET
		sm_accessible = 'N'
	WHERE
		site_code = #{siteCode}
		AND sm_name = #{smName}

