mapper.sql:
and a.delyn = 'N' and a.popup_type = #{schPopupType}
and date_format(now(), '%Y-%m-%d %H:%i:%s') >= date_format(concat(popup_sdt, ' ', popup_stm, ':00:00'), '%Y-%m-%d %H:%i:%s') and date_format(now(), '%Y-%m-%d %H:%i:%s') <= date_format(concat(popup_edt, ' ', popup_etm, ':59:59'), '%Y-%m-%d %H:%i:%s') and popup_useyn = 'Y'
and a.popup_idx = b.popup_idx and b.site_code = #{schSitecode}
and a.popup_title like concat('%' , #{schStr} , '%')
and a.popup_alt like concat('%' , #{schStr} , '%')
and ( a.popup_title like concat('%' , #{schStr} , '%') or a.popup_alt like concat('%' , #{schStr} , '%') )

mapper.select:
/*** selectMngrPopupList ***/ select a.popup_idx, a.popup_type, a.popup_title, a.popup_useyn, a.popup_mobile, a.popup_sdt, a.popup_stm, a.popup_edt, a.popup_etm, a.popup_img, a.popup_file, a.old_file_nm, a.mv_file, a.old_mv_file, a.stream_url, a.popup_alt, a.popup_url, a.popup_linktype, a.popup_width, a.popup_height, a.popup_order, a.expires, a.upload_type, a.regdt, a.upddt, a.deldt, a.regmemid, a.updmemid, a.delmemid, a.delyn, a.edit_contents from t_popup a , t_popup_site b where 1=1
order by ${@egovframework.itgcms.util.CommUtil@sqlInjetFilter(ordFld)} ${@egovframework.itgcms.util.CommUtil@sqlInjetFilter(ordBy)}
order by popup_idx desc
limit #{recordCountPerPage} offset #{firstIndex}

mapper.update:
/*** updateMngrPopupProc ***/
	update t_popup set
		 popup_title = #{popupTitle}
		, popup_useyn = #{popupUseyn}
		, popup_mobile = #{popupMobile}
		, popup_sdt = #{popupSdt}
		, popup_stm = #{popupStm}
		, popup_edt = #{popupEdt}
		, popup_etm = #{popupEtm}
		, popup_img = #{popupImg}
		, popup_file = #{popupFile}
		, old_file_nm = #{oldFileNm}
		, mv_file = #{mvFile}
	    , old_mv_file = #{oldMvFile}
	    , stream_url = #{streamUrl}
		, popup_alt = #{popupAlt}
		, popup_url = #{popupUrl}
		, popup_linktype = #{popupLinktype}
		, popup_width = #{popupWidth}
		, popup_height = #{popupHeight}
		, popup_order = #{popupOrder}
		, expires = #{expires}
		, upload_type = #{uploadType}
		, upddt = NOW()
		, updmemid = #{updmemid}
		, edit_contents = #{edit_contents}
	where
		delyn = 'N'
		and popup_idx = #{id}
		and popup_type = #{schPopupType}

/*** deleteMngrPopupProc ***/
	update t_popup set
		delyn = 'Y'
		, delmemid = #{delmemid}
		, deldt = NOW()
	where
		popup_idx = #{id}
		and popup_type = #{schPopupType}

/*** deleteMngrPopupChkProc ***/
	update t_popup set
		delyn = 'Y'
		, delmemid = #{delmemid}
		, deldt = NOW()
	where
		popup_type = #{schPopupType}
		and popup_idx  in

			#{item}

mapper.insert:
/*** mngrPopupSiteRegistProc ***/
	insert into t_popup_site(
		popup_idx,
		site_code
	 ) values(
		#{popupIdx},
		#{siteCode}
	)

mapper.delete:
/*** mngrPopupSiteDeleteProc ***/
	DELETE FROM t_popup_site
		WHERE popup_idx=#{popupIdx}
