mapper.select:
/*mngrManagerList*/
select a.mng_id, a.mng_name, a.mng_pass, a.mng_phone, a.mng_mobile, a.mng_email, a.mng_fax, a.group_code, a.position_code, a.mng_role, a.mng_power, a.mng_work, a.site_code_meta site_code, CAST(A.REGDT as DATE ) AS REGDT, CAST(A.UPDDT as DATE ) AS UPDDT, CAST(A.DELDT as DATE ) AS DELDT, a.regmemid, a.updmemid, a.delmemid, a.delyn, a.mng_ipyn, a.mng_ip, a.mng_passdt, a.mng_passtrycnt, a.mng_type, a.mng_auth, (select or_name from T_ORGANIZATION where or_code=a.GROUP_CODE) group_code_name, c.c_name position_code_name from t_manager a left join t_code b on a.group_code = b.c_code left join t_code c on a.position_code = c.c_code where 1=1
order by
a.mng_id a.mng_name b.c_name a.regdt
${@egovframework.itgcms.util.CommUtil@sqlInjetFilter(ordBy)}
order by regdt desc

mapper.update:
/*mngrManagerUpdateProc*/
update t_manager set
	 mng_email = '${@egovframework.itgcms.util.CommUtil@seedEnc256(mngEmail)}'

	, mng_pass = #{mngPass}

	, mng_phone = #{mngPhone}
	, mng_mobile = '${@egovframework.itgcms.util.CommUtil@seedEnc256(mngMobile)}'
	, mng_fax = #{mngFax}
	, group_code = #{groupCode}
	, position_code = #{positionCode}
	, mng_role = #{mngRole}
	, mng_power = #{mngPower}
	, mng_work = #{mngWork}
	, upddt = SYSDATE
	, updmemid = #{updmemid}
	, mng_ipyn = #{mngIpyn}
    , mng_ip	= #{mngIp}
    , mng_type	= #{mngType}
    , mng_auth	= #{mngAuth}
    , site_code_meta	= #{siteCodeMeta}
where
	delyn = 'N'
	and mng_id = #{id}

 	/*deleteMngrManagerProc*/
	update t_manager set
		delyn = 'Y'
		, delmemid = #{delmemid}
		, deldt = SYSDATE
	where
		mng_id = #{id}

