/*mngrManagerInitPass*/
update t_manager set
	mng_pass = #{mngPass}
	, mng_passtrycnt = 0
	, upddt = SYSDATE
	, updmemid = #{updmemid}
where mng_id = #{id}

/*increaseMngPassTryCnt*/
