/*mngrManagerLoginLogList*/
select CAST(a.log_regdt as DATE ) AS logRegdt, a.log_id logId, a.log_type logType, a.log_ip logIp, a.log_agent logAgent, a.log_os logOs, a.log_device logDevice, a.log_browser logBrowser, a.log_site logSite, CASE WHEN log_type = '1' THEN '관리자' WHEN log_type = '2' THEN '사용자' END logTypeName from t_login_log a where 1=1
order by log_regdt desc

mapper.delete:
	/*deleteMngrManagerLoginLogProc*/
	   DELETE FROM t_login_log
       WHERE TO_CHAR(log_regdt, 'YY/MM/DD') < TO_CHAR(TRUNC(SYSDATE) - 180)

