mapper.select:
/*managerListSite*/
select TA.* from ( select a.mng_id, a.mng_name, b.c_name group_code_name, c.c_name position_code_name, NVL2(e.manager_menu_auth_idx,TO_CHAR(e.manager_menu_auth_idx),'N') manager_menu_auth_idx from t_manager a left join t_code b on a.group_code = b.c_code left join t_code c on a.position_code = c.c_code left join t_manager_site d on a.mng_id = d.mng_id left join t_manager_menu_auth e on a.mng_id = e.mng_id and e.menu_auth_idx = #{schOpt1} where d.site_code = #{schSitecode}
order by a.mng_id ) TA
where TA.manager_menu_auth_idx != 'N'
where TA.manager_menu_auth_idx = 'N'

/*managerListSiteTotCnt*/
select count(*) totcnt from (
	select  a.mng_id,
			NVL2(E.manager_menu_auth_idx,TO_CHAR(E.manager_menu_auth_idx),'N') manager_menu_auth_idx
	from t_manager a
	left join t_code b on a.group_code = b.c_code
 	left join t_code c on a.position_code = b.c_code
 	left join t_manager_site d on a.mng_id = d.mng_id
 	left join t_manager_menu_auth e on a.mng_id = e.mng_id and e.menu_auth_idx = #{schOpt1}
	where d.site_code = #{schSitecode}

	) TA

  where TA.manager_menu_auth_idx != 'N'


  where TA.manager_menu_auth_idx = 'N'

/*managerListBySiteForIndividual*/
select TA.* from ( select a.mng_id, a.mng_name, b.c_name group_code_name, c.c_name position_code_name, wm_concat(f.menu_auth_name), count(case when f.menu_auth_type = '0' and f.delyn = 'N' and f.menu_auth_sitecode = #{schSitecode} then 1 end) ndv_cnt, to_char(wm_concat(case when f.menu_auth_type = '0' and f.delyn = 'N' and f.menu_auth_sitecode = #{schSitecode} then f.menu_auth_idx end)) menu_auth_idx from t_manager a left join t_code b on a.group_code = b.c_code left join t_code c on a.position_code = c.c_code left join t_manager_site d on a.mng_id = d.mng_id left join t_manager_menu_auth e on a.mng_id = e.mng_id left join t_menu_auth f on e.menu_auth_idx = f.menu_auth_idx where d.site_code = #{schSitecode}
group by a.mng_id, a.mng_name, b.c_name, c.c_name order by a.mng_id ) TA

/*selectOrgManager*/ SELECT tb2.mng_id, tb2.mng_name, tb2.mng_phone, tb2.group_order, B.c_name, B.c_order, tb2.mng_work, tb2.page, tb2.total_count FROM ( SELECT * FROM ( SELECT CEIL((ROW_NUMBER() over (ORDER BY position_code DESC))/ 10000) as page, COUNT(*) OVER() as total_count, a.mng_id, a.mng_name, a.mng_phone, a.position_code, a.group_order, a.mng_work FROM t_manager a WHERE a.delyn = 'N' AND (a.group_code = #{schCode})
AND a.mng_name like '%' || #{schStr} || '%'
AND a.mng_phone like '%' || #{schStr} || '%'
AND a.mng_work like '%' || #{schStr} || '%'
AND ( a.mng_name like '%' || #{schStr} || '%' OR a.mng_phone like '%' || #{schStr} || '%' OR a.mng_work like '%' || #{schStr} || '%' )
) tb WHERE page = #{page} ) tb2 LEFT JOIN t_code B ON B.c_code = tb2.position_code ORDER BY tb2.group_order * 1, B.c_order

/*selOrgManager*/ SELECT tb2.mng_id, tb2.mng_name, tb2.mng_phone, tb2.group_order, B.c_name, B.c_order, tb2.mng_work, tb2.page, tb2.total_count FROM ( SELECT * FROM ( SELECT CEIL((ROW_NUMBER() over (ORDER BY a.position_code DESC)) / 10000) as page, COUNT(*) OVER() as total_count, a.mng_id, a.mng_name, a.mng_phone, a.position_code, a.group_order, a.mng_work FROM t_manager a WHERE a.delyn = 'N' AND (a.group_code = #{schCode})
AND a.mng_name like '%' || #{schStr} || '%'
AND a.mng_phone like '%' || #{schStr} || '%'
AND a.mng_work like '%' || #{schStr} || '%'
AND ( a.mng_name like '%' || #{schStr} || '%' OR a.mng_phone like '%' || #{schStr} || '%' OR a.mng_work like '%' || #{schStr} || '%' )
) tb WHERE page = #{page} ) tb2 LEFT JOIN t_code B ON B.c_code = tb2.position_code ORDER BY tb2.group_order * 1, B.c_order

/*selectOrgAllManagerList*/ SELECT * FROM ( SELECT CEIL((ROW_NUMBER() over (ORDER BY mng_name asc))/ 10) as page, COUNT(*) OVER() as total_count, mng_id, mng_name, or_name, c_name, mng_phone FROM ( SELECT mng_id, mng_name, group_code, position_code, mng_phone FROM t_manager a WHERE delyn ='N' AND (group_code != #{groupCode} or group_code is null) ) tb2 LEFT JOIN t_organization A ON A.or_code = tb2.group_code LEFT JOIN t_code B ON B.c_code = tb2.position_code WHERE 1=1
and tb2.mng_id like '%' || #{schStr} || '%'
and tb2.mng_name like '%' || #{schStr} || '%'
and A.or_name like '%' || #{schStr} || '%'
and ( tb2.mng_id like '%' || #{schStr} || '%' or A.or_name like '%' || #{schStr} || '%' or tb2.mng_name like '%' || #{schStr} || '%' )
) WHERE page = #{page}

