mapper.select:
/*selectErrorMangerList*/
select a.mng_id, a.mng_name, a.mng_phone, a.mng_mobile, a.mng_email, a.group_code, a.position_code, a.mng_work, a.site_code_meta site_code, a.mng_auth, b.or_name group_code_name, c.c_name position_code_name FROM t_manager a LEFT JOIN t_organization b on a.group_code = b.or_code and b.or_del_yn = 'N' LEFT JOIN t_code c on a.position_code = c.c_code AND c.c_pfullcode = '0>programcd>cubecms>managercd>position' LEFT JOIN T_MANAGER_MENU_AUTH d on a.MNG_ID = d.MNG_ID LEFT JOIN t_menu_auth e ON d.menu_auth_idx = e.menu_auth_idx WHERE ((a.mng_auth = '80' AND e.MENU_AUTH_ITEM_META LIKE '%N41100000000%'/*오류신고메뉴코드*/) OR a.mng_auth > '80') AND a.group_code is not NULL AND a.group_code !='' AND b.or_del_yn = 'N'
GROUP BY a.mng_id, a.mng_name, a.mng_phone, a.mng_mobile, a.mng_email, a.group_code, a.position_code, a.mng_work, a.site_code_meta, a.mng_auth, b.or_name, c.c_name

/* selectManagerForCheck */ select a.mng_id, a.mng_name, a.mng_pass, a.mng_phone, a.mng_mobile, a.mng_email, a.mng_fax, a.group_code, a.position_code, a.mng_role, a.mng_power, a.mng_work, a.site_code_meta site_code, CAST(A.REGDT as DATE ) AS REGDT, CAST(A.UPDDT as DATE ) AS UPDDT, CAST(A.DELDT as DATE ) AS DELDT, a.regmemid, a.updmemid, a.delmemid, a.delyn, a.mng_ipyn, a.mng_ip, a.mng_passdt, a.mng_passtrycnt, a.mng_type, a.mng_auth from t_manager a
and a.mng_id = #{mngId}
and a.mng_name = #{mngName}
and a.mng_pass = #{mngPass}
and a.mng_email = #{mngEmail}
and a.delyn = 'N'

/*mMngrManagerListForMigration*/
